 DROP DATABASE IF EXISTS ozono;

CREATE DATABASE ozono;

USE ozono;

CREATE TABLE products(
    id INT AUTO_INCREMENT PRIMARY KEY,
    name TEXT NOT NULL,
    description_html TEXT NOT NULL,
    image_url TEXT NOT NULL,
    video_html TEXT NOT NULL
); 

